    PublicoMrecArtigosLerMais300x250 = googletag.defineSlot('/4458504/PublicoMrecArtigosLerMais300x250', [[300, 250]], 'PublicoMrecArtigosLerMais300x250Ad').addService(googletag.pubads());
});
var deferredScripts = window.deferredScripts || [];
deferredScripts.push(function () { 
    if (Foundation.MediaQuery.atLeast("large") && $("body").hasClass("is-truncated") && (typeof window.hasAds == "undefined" || window.hasAds != false)) {
        $("#story-showfull").on("click", function () {
            googletag.pubads().refresh([PublicoMrecArtigosLerMais300x250]);
        });
    }
